import React, { Component } from 'react';
import { Button, View, Text } from 'react-native';
import { createStackNavigator, createAppContainer } from 'react-navigation';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  state = {
    redirect: false
  }
  setRedirect = () => {
    this.setState({
      redirect: true
    })
  }
  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to='Output.js' />
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
        <a
            //className="App-link"
            //href="https://reactjs.org"
            //target="_blank"
            //rel="noopener noreferrer"
          >
            Weather Forecast
            <p>Enter a zipcode below to get the current weather for that area</p>
          </a>
          <img src={logo} className="App-logo" alt="logo" />
          <p className = "Enter-details">
            <form>
              <input type="text" name="postalcode" placeholder="Enter Postal Code"></input>
              {this.renderRedirect()}
              <input onClick={this.setRedirect} type="submit" value="ENTER"></input>
            </form>
          </p>
        </header>
      </div>
    );
  }
}

export default App;
